import React, { useState } from "react";
import { searchBook } from "../api/api";

const SearchBookPage = () => {
    const [last4Digits, setLast4Digits] = useState("");
    const [books, setBooks] = useState([]);
    const [message, setMessage] = useState("");

    const handleSearch = async () => {
        const response = await searchBook(last4Digits);
        if (response.error) {
            setMessage(response.error);
            setBooks([]);
        } else {
            setBooks(response);
            setMessage("");
        }
    };

    return (
        <div>
            <h2> Search a Book üîç</h2>
            <h3>Enter Last 4 Digits </h3>
            <input 
                type="text" 
                placeholder="e,g" 
                value={last4Digits} 
                onChange={(e) => setLast4Digits(e.target.value)} 
            />
            <button onClick={handleSearch}>Search</button>

            {message && <p style={{ color: "red" }}>{message}</p>}

            {books.length > 0 && (
                <table border="1" style={{ width: "100%", textAlign: "left", marginTop: "10px", borderCollapse: "collapse" }}>
                    <thead>
                        <tr style={{ backgroundColor: "#f2f2f2" }}>
                            <th>Book ID</th>
                            <th>Book Name</th>
                            <th>Author</th>
                            <th>Book Type</th>
                            <th>Edition</th>
                        </tr>
                    </thead>
                    <tbody>
                        {books.map((book) => (
                            <tr key={book.book_id}>
                                <td>{book.book_id}</td>
                                <td>{book.book_name}</td>
                                <td>{book.author}</td>
                                <td>{book.book_type}</td>
                                <td>{book.edition || "N/A"}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default SearchBookPage;
