import React, { useState } from "react";
import { addBook } from "../api/api";

const AddBookPage = () => {
    const [bookData, setBookData] = useState({
        book_name: "",
        author: "",
        book_type: "",
        edition: "",
        book_id: "",
    });

    const [message, setMessage] = useState("");

    const handleChange = (e) => {
        setBookData({ ...bookData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await addBook(bookData);
        setMessage(response.message || response.error);
    };

    return (
        <div>
            <h2>Add a New Book ðŸ“˜</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="book_name" placeholder="Book Name" onChange={handleChange} required />
                <input type="text" name="author" placeholder="Author" onChange={handleChange} required />
                <input type="text" name="book_type" placeholder="Book Type" onChange={handleChange} required />
                <input type="text" name="edition" placeholder="Edition" onChange={handleChange} required />
                <input type="text" name="book_id" placeholder="Book ID" onChange={handleChange} required />
                <button type="submit">Add Book</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
};

export default AddBookPage;
