import React, { useState } from "react";
import { searchBook } from "../api/api";

const SearchBookPage = () => {
    const [last4Digits, setLast4Digits] = useState("");
    const [books, setBooks] = useState([]);
    const [message, setMessage] = useState("");

    const handleSearch = async () => {
        const response = await searchBook(last4Digits);
        if (response.error) {
            setMessage(response.error);
            setBooks([]);
        } else {
            setBooks(response);
            setMessage("");
        }
    };

    return (
        <div>
            <h2>Search a Book üîç</h2>
            <input type="text" placeholder="Enter Last 4 Digits" onChange={(e) => setLast4Digits(e.target.value)} />
            <button onClick={handleSearch}>Search</button>
            
            {message && <p>{message}</p>}
            {books.length > 0 && (
                <ul>
                    {books.map((book) => (
                        <li key={book.book_id}>
                            <strong>{book.book_name}</strong> by {book.author} (ID: {book.book_id})
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default SearchBookPage;
