import React from "react";
import "./WelcomePage.css"; // Import CSS file

const WelcomePage = () => {
    return (
        <div className="welcome-container">
            <h2 className="fade-in">Welcome to Libro Tech ðŸ“–</h2>
            <p className="fade-in">A simple Book Search Management System.</p>

            {/* Floating Images */}
            <div className="floating-images">
                <img src="/lib1.jpg" alt="Book 1" className="floating" style={{ top: "50px", left: "10%" }} />
                <img src="/images/book2.png" alt="Book 2" className="floating" style={{ top: "120px", right: "15%" }} />
                <img src="/images/book3.png" alt="Book 3" className="floating" style={{ top: "200px", left: "30%" }} />
            </div>
        </div>
    );
};

export default WelcomePage;
